INSERT INTO admin_empresa VALUES (1, 'Link Gerencial', NULL, NULL, true, NULL, '2018-02-09 13:45:36', NULL, '<p>Estimado participante, bienvenido a&nbsp;<strong><em>Formaci&oacute;n 2.0&nbsp;</em></strong></p>

