INSERT INTO certi_opcion VALUES (1, 'Examinar las influencias, las tendencias de la tecnología y entender los cambios legales y políticos relacionados con la empresa.', NULL, 1, 1, '2018-03-30 13:09:13', '2018-03-30 13:09:13');
INSERT INTO certi_opcion VALUES (2, 'Estudiar las capacidades de la competencia y disminuir la importancia de la economía local.', NULL, 1, 1, '2018-03-30 13:09:32', '2018-03-30 13:09:32');
INSERT INTO certi_opcion VALUES (3, 'Evaluar los productos y servicios ofrecidos y disminuir la importancia de los cambios legales, relacionados con el rubro de la empresa.', NULL, 1, 1, '2018-03-30 13:09:45', '2018-03-30 13:09:45');
INSERT INTO certi_opcion VALUES (4, 'Producto, proceso, personas y precio.', NULL, 1, 1, '2018-03-30 13:10:41', '2018-03-30 13:10:41');
INSERT INTO certi_opcion VALUES (5, 'Salario, precio, personal y ofertas.', NULL, 1, 1, '2018-03-30 13:10:51', '2018-03-30 13:10:51');
INSERT INTO certi_opcion VALUES (6, 'Medios de comunicación, publicidad y grupos focales.', NULL, 1, 1, '2018-03-30 13:11:00', '2018-03-30 13:11:00');
INSERT INTO certi_opcion VALUES (7, 'Tener un desempeño superior al de la competencia. Analizar las capacidades de los competidores para identificar las posibles oportunidades y amenazas.', NULL, 1, 1, '2018-03-30 13:12:59', '2018-03-30 13:12:59');
INSERT INTO certi_opcion VALUES (8, 'Una estructura de precios bien establecida, sin importar los elementos que sean ofrecidos por la competencia.', NULL, 1, 1, '2018-03-30 13:13:11', '2018-03-30 13:13:11');
INSERT INTO certi_opcion VALUES (9, 'Fijar el desempeño en un promedio estándar, ya que al ser superior, la empresa se convertiría en una amenaza para otras organizaciones, promoviendo un ataque publicitario de los competidores.', NULL, 1, 1, '2018-03-30 13:13:28', '2018-03-30 13:13:28');
INSERT INTO certi_opcion VALUES (10, 'Examinar los procesos empresariales internos y analizar las fortalezas y las debilidades.', NULL, 1, 1, '2018-03-30 13:18:17', '2018-03-30 13:18:17');
INSERT INTO certi_opcion VALUES (11, 'Estudiar los planes de la competencia y ejecutar un mapa comparativo de servicios ofrecidos.', NULL, 1, 1, '2018-03-30 13:18:32', '2018-03-30 13:18:32');
INSERT INTO certi_opcion VALUES (12, 'Evaluar la percepción del cliente sobre la organización y mantener un foco en planos a largo plazo.', NULL, 1, 1, '2018-03-30 13:18:41', '2018-03-30 13:18:41');
INSERT INTO certi_opcion VALUES (13, 'Las implicaciones a corto y largo plazo.', NULL, 1, 1, '2018-03-30 13:24:17', '2018-03-30 13:24:17');
